import Call from './Call';
import './CallsList.scss'

let archivedCalls = [
    {
      "number": "(343)-333-2433",
      "type": "missed",
      "status": "Caller called once",
      "time":"7:50 PM"
    }
  ];
  let allCalls = 
  [
    {
      "number": "(343)-333-2433",
      "type": "missed",
      "status": "Caller called once",
      "time":"7:50 PM"
    },
    {
      "number": "(343)-333-2443",
      "type": "complete",
      "status": "Caller called once",
      "time":"7:55 PM"
    },
    {
      "number": "(343)-333-2433",
      "type": "missed",
      "status": "Caller called once",
      "time":"7:50 PM"
    },
    {
      "number": "(343)-333-2443",
      "type": "complete",
      "status": "Caller called once",
      "time":"7:55 PM"
    }
  ]

function CallsList({calls = all ? allCalls : archivedCalls}){
    const [all, setAll] = useState(true);

    const listItems = calls.map((calls, index) =>
      <Call 
        number={calls.number}
        type={calls.type} 
        status={calls.status}
        time={calls.time}
        key={index}/> 
    );

    function handleAllClick(){
        setAll(true);
    }

    function handleArchiveClick(){
        setAll(false);
    }
    return (
      <div className="callsList" >
            <div className="callsListHeader">
              <div className="callsListButton" onClick={handleAllClick}>All</div>
              <div className="callsListButton" onClick={handleArchiveClick}>Archive</div>
            </div>
          {listItems}
      </div>
    );
  }

  export default CallsList;
